import React, { useState } from 'react';
import { searchFood, getNutritionInfo } from '../utils/api';
import '../styles/FoodSearch.css';
import ChatBot from './ChatBot';

const FoodSearch = () => {
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSearch = async (e) => {
        e.preventDefault();
        if (!query.trim()) {
            setError('Please enter a search term');
            return;
        }

        setLoading(true);
        setError(null);
        setSelectedFood(null);
        
        try {
            const results = await searchFood(query);
            console.log('Search results:', results);
            
            if (Array.isArray(results) && results.length > 0) {
                setSearchResults(results);
            } else {
                setError('No results found');
                setSearchResults([]);
            }
        } catch (err) {
            console.error('Search error:', err);
            setError(err.message || 'Failed to fetch search results');
            setSearchResults([]);
        } finally {
            setLoading(false);
        }
    };

    const handleFoodSelect = async (food) => {
        try {
            setLoading(true);
            setError(null);
            console.log('Getting nutrition for food:', food);
            const nutritionData = await getNutritionInfo(food.id);
            console.log('Nutrition data received:', nutritionData);
            
            // Extract nutrition data from the response
            const nutrition = {
                protein: nutritionData?.protein || nutritionData?.nutrients?.find(n => n.name === "Protein")?.amount || 0,
                fat: nutritionData?.fat || nutritionData?.nutrients?.find(n => n.name === "Fat")?.amount || 0,
                carbs: nutritionData?.carbs || nutritionData?.nutrients?.find(n => n.name === "Carbohydrates")?.amount || 0
            };
            
            console.log('Extracted nutrition:', nutrition);
            
            setSelectedFood({
                ...food,
                nutrition
            });
        } catch (err) {
            console.error('Error fetching nutrition:', err);
            setError(err.response?.data?.message || err.message || 'Failed to fetch nutrition information');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="food-search-container">
            <div className="hero-section">
                <div className="content-area">
                    <div className="title-container">
                        <h1 className="page-title">Food Search</h1>
                        <p className="page-subtitle">
                            Discover the nutritional content of your favorite vegan foods
                        </p>
                    </div>
                    
                    <form onSubmit={handleSearch} className="search-container">
                        <input
                            type="text"
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                            placeholder="Search for food..."
                            className="search-input"
                        />
                        <button 
                            type="submit"
                            disabled={loading}
                            className="search-button"
                        >
                            {loading ? (
                                <span className="flex items-center">
                                    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                    </svg>
                                    Searching...
                                </span>
                            ) : 'Search'}
                        </button>
                    </form>

                    {error && (
                        <div className="error-message">
                            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9 7a1 1 0 112 0v5a1 1 0 11-2 0V7zm1 8a1 1 0 100 2 1 1 0 000-2z"/>
                            </svg>
                            {error}
                        </div>
                    )}

                    {searchResults.length > 0 && !selectedFood && (
                        <div className="results-container">
                            {searchResults.map((food) => (
                                <div 
                                    key={food.id}
                                    onClick={() => handleFoodSelect(food)}
                                    className="food-item"
                                >
                                    <h3 className="font-medium text-[#2C5530] capitalize">{food.name}</h3>
                                </div>
                            ))}
                        </div>
                    )}

                    {selectedFood && (
                        <div className="nutrition-card">
                            <div className="flex items-center justify-between mb-6">
                                <h2 className="text-2xl font-semibold text-[#2C5530] capitalize">
                                    {selectedFood.name}
                                </h2>
                                <button 
                                    onClick={() => setSelectedFood(null)}
                                    className="text-sm text-[#4B7F52] hover:text-[#2C5530] 
                                             px-3 py-1 rounded-md hover:bg-[#E9F5DB]
                                             transition-all duration-200"
                                >
                                    Back to results
                                </button>
                            </div>
                            {loading ? (
                                <div className="text-center py-8 text-[#4B7F52]">
                                    <div className="animate-pulse">Loading nutrition data...</div>
                                </div>
                            ) : selectedFood.nutrition ? (
                                <>
                                    <div className="grid grid-cols-3 gap-6">
                                        <div className="macro-card">
                                            <div className="font-bold text-2xl text-[#2C5530]">
                                                {selectedFood.nutrition.protein || '0'}g
                                            </div>
                                            <div className="text-sm text-[#4B7F52] font-medium">Protein</div>
                                        </div>
                                        <div className="macro-card">
                                            <div className="font-bold text-2xl text-[#2C5530]">
                                                {selectedFood.nutrition.fat || '0'}g
                                            </div>
                                            <div className="text-sm text-[#4B7F52] font-medium">Fat</div>
                                        </div>
                                        <div className="macro-card">
                                            <div className="font-bold text-2xl text-[#2C5530]">
                                                {selectedFood.nutrition.carbs || '0'}g
                                            </div>
                                            <div className="text-sm text-[#4B7F52] font-medium">Carbs</div>
                                        </div>
                                    </div>
                                    <div className="mt-6 text-center text-sm text-[#4B7F52] 
                                                  bg-[#E9F5DB] py-2 rounded-md">
                                        Nutrition values per 100g serving
                                    </div>
                                </>
                            ) : (
                                <div className="text-center py-6 text-[#B45454] bg-[#FFF5F5] rounded-lg">
                                    Failed to load nutrition data
                                </div>
                            )}
                        </div>
                    )}
                </div>
            </div>
            <ChatBot />
        </div>
    );
};

export default FoodSearch;
