import React, { useState } from 'react';
import { searchFood, getNutritionInfo } from '../utils/api';

const FoodSearch = () => {
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSearch = async (e) => {
        e.preventDefault();
        if (!query.trim()) {
            setError('Please enter a search term');
            return;
        }

        setLoading(true);
        setError(null);
        setSelectedFood(null);
        
        try {
            const results = await searchFood(query);
            console.log('Search results:', results);
            
            if (Array.isArray(results) && results.length > 0) {
                setSearchResults(results);
            } else {
                setError('No results found');
                setSearchResults([]);
            }
        } catch (err) {
            console.error('Search error:', err);
            setError(err.message || 'Failed to fetch search results');
            setSearchResults([]);
        } finally {
            setLoading(false);
        }
    };

    const handleFoodSelect = async (food) => {
        try {
            setLoading(true);
            setError(null);
            console.log('Getting nutrition for food:', food);
            const nutritionData = await getNutritionInfo(food.id);
            console.log('Nutrition data received:', nutritionData);
            
            // Extract nutrition data from the response
            const nutrition = {
                protein: nutritionData?.protein || nutritionData?.nutrients?.find(n => n.name === "Protein")?.amount || 0,
                fat: nutritionData?.fat || nutritionData?.nutrients?.find(n => n.name === "Fat")?.amount || 0,
                carbs: nutritionData?.carbs || nutritionData?.nutrients?.find(n => n.name === "Carbohydrates")?.amount || 0
            };
            
            console.log('Extracted nutrition:', nutrition);
            
            setSelectedFood({
                ...food,
                nutrition
            });
        } catch (err) {
            console.error('Error fetching nutrition:', err);
            setError(err.response?.data?.message || err.message || 'Failed to fetch nutrition information');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-green-50 via-emerald-50 to-teal-50 py-8 px-4">
            <div className="max-w-2xl mx-auto">
                <div className="text-center mb-8">
                    <h1 className="text-4xl font-bold text-gray-800 mb-2 tracking-tight">
                        Food Search
                    </h1>
                    <p className="text-gray-600">
                        Search for foods to view their nutritional information
                    </p>
                </div>
                
                <form onSubmit={handleSearch} className="mb-8">
                    <div className="flex gap-3">
                        <input
                            type="text"
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                            placeholder="Search for food..."
                            className="flex-1 p-3 border border-gray-200 rounded-lg shadow-sm 
                                     focus:ring-2 focus:ring-green-500 focus:border-transparent
                                     transition-all duration-200 bg-white/80 backdrop-blur-sm"
                        />
                        <button 
                            type="submit"
                            disabled={loading}
                            className="px-6 py-3 bg-gradient-to-r from-green-500 to-emerald-600 
                                     text-white rounded-lg shadow-sm hover:shadow-md 
                                     transition-all duration-200 disabled:opacity-50
                                     hover:translate-y-[-1px] active:translate-y-[1px]"
                        >
                            {loading ? 'Searching...' : 'Search'}
                        </button>
                    </div>
                </form>

                {error && (
                    <div className="p-4 mb-6 text-red-700 bg-red-50 rounded-lg border border-red-100 shadow-sm">
                        {error}
                    </div>
                )}

                {searchResults.length > 0 && !selectedFood && (
                    <div className="space-y-3">
                        {searchResults.map((food) => (
                            <div 
                                key={food.id}
                                onClick={() => handleFoodSelect(food)}
                                className="p-4 border border-gray-100 rounded-lg hover:bg-white/80 
                                         cursor-pointer transition-all duration-200 hover:shadow-md
                                         bg-white/60 backdrop-blur-sm hover:translate-x-1"
                            >
                                <h3 className="font-semibold text-gray-800 capitalize">{food.name}</h3>
                            </div>
                        ))}
                    </div>
                )}

                {selectedFood && (
                    <div className="bg-white/80 backdrop-blur-sm p-6 rounded-xl shadow-lg border border-gray-100">
                        <div className="flex items-center justify-between mb-6">
                            <h2 className="text-2xl font-bold text-gray-800 capitalize">{selectedFood.name}</h2>
                            <button 
                                onClick={() => setSelectedFood(null)}
                                className="text-sm text-gray-500 hover:text-gray-700 
                                         px-3 py-1 rounded-md hover:bg-gray-100
                                         transition-all duration-200"
                            >
                                Back to results
                            </button>
                        </div>
                        {loading ? (
                            <div className="text-center py-8 text-gray-600">
                                <div className="animate-pulse">Loading nutrition data...</div>
                            </div>
                        ) : selectedFood.nutrition ? (
                            <>
                                <div className="grid grid-cols-3 gap-6">
                                    <div className="bg-gradient-to-br from-blue-50 to-blue-100 p-4 rounded-lg shadow-sm">
                                        <div className="font-bold text-2xl text-blue-700">
                                            {selectedFood.nutrition.protein || '0'}g
                                        </div>
                                        <div className="text-sm text-blue-600 font-medium">Protein</div>
                                    </div>
                                    <div className="bg-gradient-to-br from-amber-50 to-amber-100 p-4 rounded-lg shadow-sm">
                                        <div className="font-bold text-2xl text-amber-700">
                                            {selectedFood.nutrition.fat || '0'}g
                                        </div>
                                        <div className="text-sm text-amber-600 font-medium">Fat</div>
                                    </div>
                                    <div className="bg-gradient-to-br from-emerald-50 to-emerald-100 p-4 rounded-lg shadow-sm">
                                        <div className="font-bold text-2xl text-emerald-700">
                                            {selectedFood.nutrition.carbs || '0'}g
                                        </div>
                                        <div className="text-sm text-emerald-600 font-medium">Carbs</div>
                                    </div>
                                </div>
                                <div className="mt-6 text-center text-sm text-gray-500 bg-gray-50 py-2 rounded-md">
                                    Nutrition values per 100g serving
                                </div>
                            </>
                        ) : (
                            <div className="text-center py-6 text-red-600 bg-red-50 rounded-lg">
                                Failed to load nutrition data
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default FoodSearch;
