import React, { createContext, useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const NutritionContext = createContext();

export const NutritionProvider = ({ children }) => {
  const navigate = useNavigate();
  const [requirements, setRequirements] = useState(null);
  const [selectedFoods, setSelectedFoods] = useState([]);

  const setUserRequirements = (calculatorResults) => {
    setRequirements({
      dailyCalories: calculatorResults.dailyCalories,
      remainingCalories: calculatorResults.dailyCalories,
      macros: {
        ...calculatorResults.macros,
        remainingCarbs: calculatorResults.macros.carbsGrams,
        remainingProtein: calculatorResults.macros.proteinGrams,
        remainingFat: calculatorResults.macros.fatGrams,
      },
      bmi: calculatorResults.bmi
    });
    // Redirect to food search after setting requirements
    navigate('/food-search');
  };

  const addFood = (food) => {
    setSelectedFoods(prev => [...prev, food]);
    setRequirements(prev => ({
      ...prev,
      remainingCalories: prev.remainingCalories - food.calories,
      macros: {
        ...prev.macros,
        remainingCarbs: prev.macros.remainingCarbs - food.carbs,
        remainingProtein: prev.macros.remainingProtein - food.protein,
        remainingFat: prev.macros.remainingFat - food.fat
      }
    }));
  };

  const removeFood = (foodIndex) => {
    const removedFood = selectedFoods[foodIndex];
    setSelectedFoods(prev => prev.filter((_, index) => index !== foodIndex));
    setRequirements(prev => ({
      ...prev,
      remainingCalories: prev.remainingCalories + removedFood.calories,
      macros: {
        ...prev.macros,
        remainingCarbs: prev.macros.remainingCarbs + removedFood.carbs,
        remainingProtein: prev.macros.remainingProtein + removedFood.protein,
        remainingFat: prev.macros.remainingFat + removedFood.fat
      }
    }));
  };

  return (
    <NutritionContext.Provider 
      value={{ 
        requirements,
        selectedFoods,
        setUserRequirements,
        addFood,
        removeFood
      }}
    >
      {children}
    </NutritionContext.Provider>
  );
};

export const useNutrition = () => {
  const context = useContext(NutritionContext);
  if (!context) {
    throw new Error('useNutrition must be used within a NutritionProvider');
  }
  return context;
};
