import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider } from '@react-oauth/google';
import { Routes, Route, Navigate } from 'react-router-dom';
import GoogleLogin from './components/GoogleLogin';
import Header from './components/Header';
import MealTracker from './components/MealTracker';
import Calculator from './components/Calculator';
import { useTheme } from './context/ThemeContext';
import { NutritionProvider } from './context/NutritionContext';
import { Link } from 'react-router-dom';
import Login from './components/Login';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [dailyMeals, setDailyMeals] = useState([]);
  const { darkMode } = useTheme();

  useEffect(() => {
    // Check if user is authenticated
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleLoginSuccess = (response) => {
    console.log('Login successful:', response);
    setIsAuthenticated(true);
  };

  const handleAddMeal = (newMeal) => {
    setDailyMeals(prev => [...prev, newMeal]);
  };

  const handleRemoveMeal = (mealIndex) => {
    setDailyMeals(prev => prev.filter((_, index) => index !== mealIndex));
  };

  // Protected Route component
  const ProtectedRoute = ({ children }) => {
    const token = localStorage.getItem('token');
    if (!token) {
      return <Navigate to="/login" />;
    }
    return children;
  };

  if (!isAuthenticated) {
    return (
      <GoogleOAuthProvider clientId="591276305061-4b3vrn5921v0770c45kvsbb739gb6i1v.apps.googleusercontent.com">
        <Login onLoginSuccess={handleLoginSuccess} />
      </GoogleOAuthProvider>
    );
  }

  return (
    <GoogleOAuthProvider clientId="591276305061-4b3vrn5921v0770c45kvsbb739gb6i1v.apps.googleusercontent.com">
      <div className={`min-h-screen ${darkMode ? 'bg-gray-900' : 'bg-gray-50'} transition-colors duration-200`}>
        <Header />
        <nav className={`${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-sm`}>
          <div className="container mx-auto px-4">
            <ul className="flex space-x-8 py-4">
              <li>
                <Link to="/" className={`${darkMode ? 'text-green-400 hover:text-green-300' : 'text-green-600 hover:text-green-800'} transition-colors`}>
                  Food Search
                </Link>
              </li>
              <li>
                <Link to="/calculator" className={`${darkMode ? 'text-green-400 hover:text-green-300' : 'text-green-600 hover:text-green-800'} transition-colors`}>
                  BMI & Calorie Calculator
                </Link>
              </li>
            </ul>
          </div>
        </nav>
        <main className="container mx-auto px-4 py-8">
          <Routes>
            <Route
              path="/login"
              element={<Login onLoginSuccess={handleLoginSuccess} />}
            />
            <Route
              path="/"
              element={
                <ProtectedRoute>
                  <MealTracker 
                    meals={dailyMeals}
                    onAddMeal={handleAddMeal}
                    onRemoveMeal={handleRemoveMeal}
                  />
                </ProtectedRoute>
              }
            />
            <Route
              path="/calculator"
              element={
                <ProtectedRoute>
                  <Calculator />
                </ProtectedRoute>
              }
            />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
