import React, { useState } from 'react';
import { searchFood, getNutritionInfo } from '../utils/api';

const FoodSearch = () => {
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSearch = async (e) => {
        e.preventDefault();
        if (!query.trim()) {
            setError('Please enter a search term');
            return;
        }

        setLoading(true);
        setError(null);
        setSelectedFood(null);
        
        try {
            const results = await searchFood(query);
            console.log('Search results:', results);
            
            if (Array.isArray(results) && results.length > 0) {
                setSearchResults(results);
            } else {
                setError('No results found');
                setSearchResults([]);
            }
        } catch (err) {
            console.error('Search error:', err);
            setError(err.message || 'Failed to fetch search results');
            setSearchResults([]);
        } finally {
            setLoading(false);
        }
    };

    const handleFoodSelect = async (food) => {
        try {
            setLoading(true);
            setError(null);
            console.log('Getting nutrition for food:', food);
            const nutritionData = await getNutritionInfo(food.id);
            console.log('Nutrition data received:', nutritionData);
            setSelectedFood({
                ...food,
                nutrition: nutritionData
            });
        } catch (err) {
            console.error('Error fetching nutrition:', err);
            setError('Failed to fetch nutrition information');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto p-4">
            <h1 className="text-3xl font-bold mb-6">Food Search</h1>
            
            <form onSubmit={handleSearch} className="mb-6">
                <div className="flex gap-2">
                    <input
                        type="text"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder="Search for food..."
                        className="flex-1 p-2 border rounded"
                    />
                    <button 
                        type="submit"
                        disabled={loading}
                        className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 disabled:bg-green-300"
                    >
                        {loading ? 'Searching...' : 'Search'}
                    </button>
                </div>
            </form>

            {error && (
                <div className="p-4 mb-4 text-red-700 bg-red-100 rounded">
                    {error}
                </div>
            )}

            {searchResults.length > 0 && !selectedFood && (
                <div className="space-y-4">
                    {searchResults.map((food) => (
                        <div 
                            key={food.id}
                            className="p-4 border rounded hover:bg-gray-50 cursor-pointer"
                            onClick={() => handleFoodSelect(food)}
                        >
                            <h3 className="font-semibold">{food.name}</h3>
                        </div>
                    ))}
                </div>
            )}

            {selectedFood && (
                <div className="bg-gray-50 p-4 rounded">
                    <div className="flex items-center justify-between mb-4">
                        <h2 className="text-xl font-semibold">{selectedFood.name}</h2>
                        <button 
                            onClick={() => setSelectedFood(null)}
                            className="text-sm text-gray-500 hover:text-gray-700"
                        >
                            Back to results
                        </button>
                    </div>
                    {loading ? (
                        <div className="text-center py-4">Loading nutrition data...</div>
                    ) : selectedFood.nutrition ? (
                        <>
                            <div className="grid grid-cols-3 gap-4 text-center">
                                <div className="bg-blue-100 p-3 rounded">
                                    <div className="font-bold text-lg">
                                        {selectedFood.nutrition.protein || '0'}g
                                    </div>
                                    <div className="text-sm">Protein</div>
                                </div>
                                <div className="bg-yellow-100 p-3 rounded">
                                    <div className="font-bold text-lg">
                                        {selectedFood.nutrition.fat || '0'}g
                                    </div>
                                    <div className="text-sm">Fat</div>
                                </div>
                                <div className="bg-green-100 p-3 rounded">
                                    <div className="font-bold text-lg">
                                        {selectedFood.nutrition.carbs || '0'}g
                                    </div>
                                    <div className="text-sm">Carbs</div>
                                </div>
                            </div>
                            <div className="mt-4 text-center text-sm text-gray-500">
                                Nutrition values per 100g serving
                            </div>
                        </>
                    ) : (
                        <div className="text-center py-4 text-red-600">
                            Failed to load nutrition data
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default FoodSearch;
