import config from '../config/config';
import { GoogleGenerativeAI } from '@google/generative-ai';

const SYSTEM_PROMPT = `You are a helpful vegan meal assistant. You can:
1. Suggest vegan recipes and meal ideas
2. Provide nutritional information about vegan ingredients
3. Offer tips for vegan meal prep and cooking
4. Answer questions about vegan nutrition and diet
5. Help with vegan substitutions for non-vegan ingredients

Keep responses concise and friendly. If you're not sure about something, say so.`;

// Initialize Gemini AI
const genAI = new GoogleGenerativeAI(config.GEMINI_API_KEY);
const model = genAI.getGenerativeModel({ model: "gemini-pro" });

export const getChatResponse = async (messages) => {
    try {
        // Combine system prompt with user message
        const latestMessage = messages[messages.length - 1];
        const fullPrompt = `${SYSTEM_PROMPT}\n\nUser: ${latestMessage.text}\n\nAssistant:`;

        // Generate content using the chat model
        const result = await model.generateContent(fullPrompt);
        const response = await result.response;
        const text = response.text();

        console.log('API Response:', text);
        
        if (text) {
            return text;
        } else {
            throw new Error('No response text received from AI');
        }
    } catch (error) {
        console.error('Chat API Error:', error);
        throw error;
    }
};
