import React from 'react';
import {
  ChartBarIcon,
  FireIcon,
  BeakerIcon,
  ScaleIcon
} from '@heroicons/react/24/outline';

function NutritionSummary({ totals }) {
  const nutritionItems = [
    {
      label: 'Calories',
      value: Math.round(totals.calories),
      unit: 'kcal',
      icon: FireIcon,
      color: 'text-orange-600',
      bgColor: 'bg-orange-100'
    },
    {
      label: 'Protein',
      value: Math.round(totals.protein),
      unit: 'g',
      icon: ChartBarIcon,
      color: 'text-blue-600',
      bgColor: 'bg-blue-100'
    },
    {
      label: 'Carbs',
      value: Math.round(totals.carbs),
      unit: 'g',
      icon: BeakerIcon,
      color: 'text-green-600',
      bgColor: 'bg-green-100'
    },
    {
      label: 'Fat',
      value: Math.round(totals.fat),
      unit: 'g',
      icon: ScaleIcon,
      color: 'text-purple-600',
      bgColor: 'bg-purple-100'
    }
  ];

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <h2 className="text-xl font-semibold mb-4">Daily Nutrition Summary</h2>
      
      <div className="space-y-4">
        {nutritionItems.map((item) => {
          const Icon = item.icon;
          return (
            <div
              key={item.label}
              className="flex items-center p-4 rounded-lg border border-gray-200"
            >
              <div className={`p-3 rounded-full ${item.bgColor} mr-4`}>
                <Icon className={`h-6 w-6 ${item.color}`} />
              </div>
              <div>
                <p className="text-sm text-gray-600">{item.label}</p>
                <p className="text-xl font-semibold">
                  {item.value}
                  <span className="text-sm font-normal text-gray-600 ml-1">
                    {item.unit}
                  </span>
                </p>
              </div>
            </div>
          );
        })}
      </div>

      <div className="mt-6 p-4 bg-primary-50 rounded-lg">
        <h3 className="text-sm font-medium text-primary-800 mb-2">
          Daily Goals
        </h3>
        <div className="space-y-2">
          <div className="h-2 bg-gray-200 rounded-full">
            <div
              className="h-2 bg-primary-600 rounded-full"
              style={{
                width: `${Math.min((totals.calories / 2000) * 100, 100)}%`
              }}
            ></div>
          </div>
          <div className="flex justify-between text-xs text-gray-600">
            <span>{Math.round((totals.calories / 2000) * 100)}% of daily goal</span>
            <span>2000 kcal</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default NutritionSummary;
