import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useNutrition } from '../context/NutritionContext';
import { calculateRequirements, saveUserRequirements } from '../utils/api';

const Calculator = () => {
  const navigate = useNavigate();
  const { setUserRequirements } = useNutrition();
  const [formData, setFormData] = useState({
    gender: 'male',
    age: '',
    heightCm: '',
    weightKg: '',
    activityLevel: 'sedentary'
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [results, setResults] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // First calculate the requirements
      const calculatedResults = await calculateRequirements(formData);
      setResults(calculatedResults);

      // Save user data and requirements to MongoDB
      const userData = {
        ...formData,
        requirements: calculatedResults
      };
      
      const userResponse = await saveUserRequirements(userData);
      
      // Store user ID in local storage for future reference
      if (userResponse?._id) {
        localStorage.setItem('userId', userResponse._id);
      }
      
      // Update context with the calculated results
      setUserRequirements(calculatedResults);
    } catch (err) {
      console.error('Calculator error:', err);
      setError(err.userMessage || 'Failed to calculate. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleNavigateToFoodSearch = () => {
    navigate('/food-search');
  };

  const getBMICategory = (bmi) => {
    if (bmi < 18.5) return 'Underweight';
    if (bmi < 25) return 'Normal weight';
    if (bmi < 30) return 'Overweight';
    return 'Obese';
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-3xl font-bold text-green-800 mb-6">Calculate Your Daily Requirements</h2>
      <p className="text-gray-600 mb-8">
        Let's start by calculating your daily caloric and macro requirements. 
        After this, you'll be able to search and track your food intake.
      </p>
      
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Gender
            </label>
            <select
              name="gender"
              value={formData.gender}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded-md"
              required
            >
              <option value="male">Male</option>
              <option value="female">Female</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Age
            </label>
            <input
              type="number"
              name="age"
              value={formData.age}
              onChange={handleChange}
              min="1"
              max="120"
              className="w-full p-2 border border-gray-300 rounded-md"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Height (cm)
            </label>
            <input
              type="number"
              name="heightCm"
              value={formData.heightCm}
              onChange={handleChange}
              min="1"
              className="w-full p-2 border border-gray-300 rounded-md"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Weight (kg)
            </label>
            <input
              type="number"
              name="weightKg"
              value={formData.weightKg}
              onChange={handleChange}
              min="1"
              className="w-full p-2 border border-gray-300 rounded-md"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Activity Level
            </label>
            <select
              name="activityLevel"
              value={formData.activityLevel}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded-md"
              required
            >
              <option value="sedentary">Sedentary</option>
              <option value="lightly active">Lightly Active</option>
              <option value="moderately active">Moderately Active</option>
              <option value="very active">Very Active</option>
            </select>
          </div>
        </div>

        <div className="flex gap-4">
          <button
            type="submit"
            disabled={loading}
            className="flex-1 bg-green-600 text-white py-3 px-4 rounded-md hover:bg-green-700 transition-colors disabled:bg-green-400 text-lg font-medium"
          >
            {loading ? 'Calculating...' : 'Calculate Requirements'}
          </button>

          {results && (
            <button
              type="button"
              onClick={handleNavigateToFoodSearch}
              className="flex-1 bg-blue-600 text-white py-3 px-4 rounded-md hover:bg-blue-700 transition-colors text-lg font-medium"
            >
              Continue to Food Search
            </button>
          )}
        </div>
      </form>

      {error && (
        <div className="mt-6 p-4 bg-red-100 text-red-700 rounded-md">
          {error}
        </div>
      )}

      {results && (
        <div className="mt-8 space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="p-6 bg-green-50 rounded-lg">
              <h3 className="text-xl font-semibold text-green-800 mb-4">BMI Results</h3>
              <p className="text-lg">BMI: {results.bmi.toFixed(1)}</p>
              <p className="text-md text-green-600">Category: {getBMICategory(results.bmi)}</p>
            </div>

            <div className="p-6 bg-green-50 rounded-lg">
              <h3 className="text-xl font-semibold text-green-800 mb-4">Daily Calories</h3>
              <p className="text-lg">{Math.round(results.dailyCalories)} calories/day</p>
            </div>
          </div>

          <div className="p-6 bg-green-50 rounded-lg">
            <h3 className="text-xl font-semibold text-green-800 mb-4">Macro Breakdown</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <p className="font-medium">Carbohydrates</p>
                <p>{Math.round(results.macros.carbsGrams)}g (40%)</p>
              </div>
              <div>
                <p className="font-medium">Protein</p>
                <p>{Math.round(results.macros.proteinGrams)}g (30%)</p>
              </div>
              <div>
                <p className="font-medium">Fat</p>
                <p>{Math.round(results.macros.fatGrams)}g (30%)</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Calculator;
