import React, { useState } from 'react';

const FoodSearch = () => {
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSearch = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        setSelectedFood(null);
        try {
            const response = await fetch(`http://localhost:8080/api/food/search?query=${encodeURIComponent(query)}`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            console.log('Search response:', data); // Debug log
            if (data && Array.isArray(data.results)) {
                setSearchResults(data.results);
                if (data.results.length === 0) {
                    setError('No results found');
                }
            } else {
                throw new Error('Invalid response format');
            }
        } catch (err) {
            console.error('Error:', err);
            setError('Failed to fetch search results');
            setSearchResults([]);
        } finally {
            setLoading(false);
        }
    };

    const handleFoodSelect = async (food) => {
        try {
            setLoading(true);
            const response = await fetch(`http://localhost:8080/api/food/nutrition/${food.id}`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const nutritionData = await response.json();
            console.log('Nutrition response:', nutritionData); // Debug log
            setSelectedFood({
                ...food,
                nutrition: nutritionData
            });
        } catch (err) {
            console.error('Error:', err);
            setError('Failed to fetch nutrition information');
        } finally {
            setLoading(false);
        }
    };

    const getNutrientAmount = (nutrients, nutrientName) => {
        if (!nutrients) return '0';
        const nutrient = nutrients.find(n => n.name.toLowerCase() === nutrientName.toLowerCase());
        return nutrient ? nutrient.amount.toFixed(1) : '0';
    };

    return (
        <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg shadow-lg">
            <h1 className="text-2xl font-bold mb-6 text-center">Food Search</h1>
            
            <form onSubmit={handleSearch} className="mb-6">
                <div className="flex gap-2">
                    <input
                        type="text"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder="Enter a food item..."
                        className="flex-1 p-2 border rounded"
                        required
                    />
                    <button 
                        type="submit"
                        disabled={loading}
                        className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 disabled:bg-green-300"
                    >
                        {loading ? 'Searching...' : 'Search'}
                    </button>
                </div>
            </form>

            {error && (
                <div className="text-red-500 text-center mb-4">{error}</div>
            )}

            {searchResults.length > 0 && !selectedFood && (
                <div className="mb-6">
                    <h2 className="text-lg font-semibold mb-2">Search Results:</h2>
                    <div className="space-y-2">
                        {searchResults.map((food) => (
                            <button
                                key={food.id}
                                onClick={() => handleFoodSelect(food)}
                                className="w-full text-left p-2 hover:bg-gray-100 rounded transition-colors"
                            >
                                {food.name}
                            </button>
                        ))}
                    </div>
                </div>
            )}

            {selectedFood && selectedFood.nutrition && (
                <div className="bg-gray-50 p-4 rounded">
                    <div className="flex items-center justify-between mb-4">
                        <h2 className="text-xl font-semibold">{selectedFood.name}</h2>
                        <button 
                            onClick={() => setSelectedFood(null)}
                            className="text-sm text-gray-500 hover:text-gray-700"
                        >
                            Back to results
                        </button>
                    </div>
                    <div className="grid grid-cols-3 gap-4 text-center">
                        <div className="bg-blue-100 p-3 rounded">
                            <div className="font-bold text-lg">
                                {getNutrientAmount(selectedFood.nutrition.nutrients, 'Protein')}g
                            </div>
                            <div className="text-sm">Protein</div>
                        </div>
                        <div className="bg-yellow-100 p-3 rounded">
                            <div className="font-bold text-lg">
                                {getNutrientAmount(selectedFood.nutrition.nutrients, 'Fat')}g
                            </div>
                            <div className="text-sm">Fat</div>
                        </div>
                        <div className="bg-green-100 p-3 rounded">
                            <div className="font-bold text-lg">
                                {getNutrientAmount(selectedFood.nutrition.nutrients, 'Carbohydrates')}g
                            </div>
                            <div className="text-sm">Carbs</div>
                        </div>
                    </div>
                    <div className="mt-4 text-center text-sm text-gray-500">
                        Nutrition values per 100g serving
                    </div>
                </div>
            )}
        </div>
    );
};

export default FoodSearch;
