import config from '../config/config';

const SYSTEM_PROMPT = `You are a helpful vegan meal assistant. You can:
1. Suggest vegan recipes and meal ideas
2. Provide nutritional information about vegan ingredients
3. Offer tips for vegan meal prep and cooking
4. Answer questions about vegan nutrition and diet
5. Help with vegan substitutions for non-vegan ingredients

Keep responses concise and friendly. If you're not sure about something, say so.`;

export const getChatResponse = async (messages) => {
    try {
        const response = await fetch('https://api.anthropic.com/v1/messages', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'x-api-key': config.ANTHROPIC_API_KEY,
                'anthropic-version': '2023-06-01'
            },
            body: JSON.stringify({
                model: "claude-3-opus-20240229",
                max_tokens: 300,
                messages: [
                    {
                        role: "system",
                        content: SYSTEM_PROMPT
                    },
                    ...messages.map(msg => ({
                        role: msg.isBot ? "assistant" : "user",
                        content: msg.text
                    }))
                ]
            })
        });

        if (!response.ok) {
            const error = await response.json();
            throw new Error(error.error?.message || 'Failed to get response from AI');
        }

        const data = await response.json();
        return data.content[0].text;
    } catch (error) {
        console.error('Chat API Error:', error);
        throw error;
    }
};
