import React, { useState } from 'react';
import { searchFood, getNutritionInfo } from '../utils/api';
import '../styles/FoodSearch.css';

const LeafIcon = () => (
    <svg className="leaf-icon" width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
        <path d="M17.75 21.75c-4.14 0-7.5-3.36-7.5-7.5 0-3.79 2.85-6.88 6.44-7.44.02-.01.04-.01.06-.01.24-.03.5-.05.75-.05 4.14 0 7.5 3.36 7.5 7.5s-3.36 7.5-7.5 7.5zm-6.44-7.5c0 3.31 2.69 6 6 6s6-2.69 6-6-2.69-6-6-6c-.2 0-.39.01-.58.04-3.01.46-5.42 3.01-5.42 5.96z"/>
    </svg>
);

const FoodSearch = () => {
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSearch = async (e) => {
        e.preventDefault();
        if (!query.trim()) {
            setError('Please enter a search term');
            return;
        }

        setLoading(true);
        setError(null);
        setSelectedFood(null);
        
        try {
            const results = await searchFood(query);
            console.log('Search results:', results);
            
            if (Array.isArray(results) && results.length > 0) {
                setSearchResults(results);
            } else {
                setError('No results found');
                setSearchResults([]);
            }
        } catch (err) {
            console.error('Search error:', err);
            setError(err.message || 'Failed to fetch search results');
            setSearchResults([]);
        } finally {
            setLoading(false);
        }
    };

    const handleFoodSelect = async (food) => {
        try {
            setLoading(true);
            setError(null);
            console.log('Getting nutrition for food:', food);
            const nutritionData = await getNutritionInfo(food.id);
            console.log('Nutrition data received:', nutritionData);
            
            // Extract nutrition data from the response
            const nutrition = {
                protein: nutritionData?.protein || nutritionData?.nutrients?.find(n => n.name === "Protein")?.amount || 0,
                fat: nutritionData?.fat || nutritionData?.nutrients?.find(n => n.name === "Fat")?.amount || 0,
                carbs: nutritionData?.carbs || nutritionData?.nutrients?.find(n => n.name === "Carbohydrates")?.amount || 0
            };
            
            console.log('Extracted nutrition:', nutrition);
            
            setSelectedFood({
                ...food,
                nutrition
            });
        } catch (err) {
            console.error('Error fetching nutrition:', err);
            setError(err.response?.data?.message || err.message || 'Failed to fetch nutrition information');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen food-search-container font-['Poppins'] py-8 px-4">
            <div className="max-w-2xl mx-auto relative z-10">
                <div className="text-center mb-8">
                    <h1 className="text-4xl font-bold text-[#2C5530] mb-2 tracking-tight flex items-center justify-center">
                        <LeafIcon /> Food Search
                    </h1>
                    <p className="text-[#4B7F52] font-light">
                        Discover the nutritional content of your favorite vegan foods
                    </p>
                </div>
                
                <form onSubmit={handleSearch} className="mb-8">
                    <div className="flex gap-3">
                        <input
                            type="text"
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                            placeholder="Search for food..."
                            className="flex-1 p-3 rounded-lg shadow-sm search-input
                                     bg-white/90 backdrop-blur-sm text-[#2C5530]
                                     placeholder-[#88AB8E] outline-none"
                        />
                        <button 
                            type="submit"
                            disabled={loading}
                            className="px-6 py-3 bg-[#4B7F52] hover:bg-[#2C5530] 
                                     text-white rounded-lg shadow-sm hover:shadow-md 
                                     transition-all duration-200 disabled:opacity-50
                                     flex items-center gap-2"
                        >
                            {loading ? 'Searching...' : (
                                <>
                                    <LeafIcon />
                                    Search
                                </>
                            )}
                        </button>
                    </div>
                </form>

                {error && (
                    <div className="p-4 mb-6 text-[#B45454] bg-[#FFF5F5] rounded-lg 
                                  border border-[#FFE5E5] shadow-sm error-message
                                  flex items-center">
                        <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9 7a1 1 0 112 0v5a1 1 0 11-2 0V7zm1 8a1 1 0 100 2 1 1 0 000-2z"/>
                        </svg>
                        {error}
                    </div>
                )}

                {searchResults.length > 0 && !selectedFood && (
                    <div className="space-y-3">
                        {searchResults.map((food) => (
                            <div 
                                key={food.id}
                                onClick={() => handleFoodSelect(food)}
                                className="food-item p-4 rounded-lg cursor-pointer
                                         bg-white/80 backdrop-blur-sm"
                            >
                                <h3 className="font-medium text-[#2C5530] capitalize">{food.name}</h3>
                            </div>
                        ))}
                    </div>
                )}

                {selectedFood && (
                    <div className="bg-white/90 backdrop-blur-sm p-6 rounded-xl shadow-md border border-[#E9F5DB]">
                        <div className="flex items-center justify-between mb-6">
                            <h2 className="text-2xl font-semibold text-[#2C5530] capitalize flex items-center">
                                <LeafIcon />
                                {selectedFood.name}
                            </h2>
                            <button 
                                onClick={() => setSelectedFood(null)}
                                className="text-sm text-[#4B7F52] hover:text-[#2C5530] 
                                         px-3 py-1 rounded-md hover:bg-[#E9F5DB]
                                         transition-all duration-200"
                            >
                                Back to results
                            </button>
                        </div>
                        {loading ? (
                            <div className="text-center py-8 text-[#4B7F52]">
                                <div className="animate-pulse">Loading nutrition data...</div>
                            </div>
                        ) : selectedFood.nutrition ? (
                            <>
                                <div className="grid grid-cols-3 gap-6">
                                    <div className="macro-card bg-gradient-to-br from-[#E9F5DB] to-[#A8D5BA] 
                                                 p-4 rounded-lg shadow-sm text-center">
                                        <div className="font-bold text-2xl text-[#2C5530]">
                                            {selectedFood.nutrition.protein || '0'}g
                                        </div>
                                        <div className="text-sm text-[#4B7F52] font-medium">Protein</div>
                                    </div>
                                    <div className="macro-card bg-gradient-to-br from-[#E9F5DB] to-[#A8D5BA] 
                                                 p-4 rounded-lg shadow-sm text-center">
                                        <div className="font-bold text-2xl text-[#2C5530]">
                                            {selectedFood.nutrition.fat || '0'}g
                                        </div>
                                        <div className="text-sm text-[#4B7F52] font-medium">Fat</div>
                                    </div>
                                    <div className="macro-card bg-gradient-to-br from-[#E9F5DB] to-[#A8D5BA] 
                                                 p-4 rounded-lg shadow-sm text-center">
                                        <div className="font-bold text-2xl text-[#2C5530]">
                                            {selectedFood.nutrition.carbs || '0'}g
                                        </div>
                                        <div className="text-sm text-[#4B7F52] font-medium">Carbs</div>
                                    </div>
                                </div>
                                <div className="mt-6 text-center text-sm text-[#4B7F52] 
                                              bg-[#E9F5DB] py-2 rounded-md">
                                    Nutrition values per 100g serving
                                </div>
                            </>
                        ) : (
                            <div className="text-center py-6 text-[#B45454] bg-[#FFF5F5] rounded-lg">
                                Failed to load nutrition data
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default FoodSearch;
