import React, { useState } from 'react';
import { MagnifyingGlassIcon } from '@heroicons/react/24/outline';
import { toast } from 'react-toastify';
import { searchFood, getNutritionInfo } from '../utils/api';
import { useTheme } from '../context/ThemeContext';
import ChatBot from './ChatBot';

function MealTracker({ meals, onAddMeal, onRemoveMeal }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedFood, setSelectedFood] = useState(null);
  const [loading, setLoading] = useState(false);
  const { darkMode } = useTheme();

  const totalNutrition = meals.reduce((acc, meal) => ({
    calories: acc.calories + (meal.nutrition?.calories || 0),
    protein: acc.protein + (meal.nutrition?.protein || 0),
    carbs: acc.carbs + (meal.nutrition?.carbs || 0),
    fat: acc.fat + (meal.nutrition?.fat || 0)
  }), { calories: 0, protein: 0, carbs: 0, fat: 0 });

  const handleSearch = async (query) => {
    if (!query || query.length < 2) {
      toast.info('Please enter at least 2 characters to search');
      setSearchResults([]);
      setSelectedFood(null);
      return;
    }

    setLoading(true);
    try {
      const results = await searchFood(query);
      if (results && results.length > 0) {
        setSearchResults(results);
        setSelectedFood(null);
      } else {
        setSearchResults([]);
        setSelectedFood(null);
        toast.info('No food items found. Try a different search term.');
      }
    } catch (error) {
      if (error.response?.status !== 404) {
        toast.error('Error searching for food items');
        console.error('Search error:', error);
      }
      setSearchResults([]);
      setSelectedFood(null);
    } finally {
      setLoading(false);
    }
  };

  const handleSelectFood = async (food) => {
    setLoading(true);
    try {
      const nutritionInfo = await getNutritionInfo(food.id);
      setSelectedFood({
        ...food,
        nutrition: nutritionInfo
      });
    } catch (error) {
      toast.error('Error getting nutrition information');
      console.error('Nutrition info error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSearch(searchTerm);
    }
  };

  return (
    <div className="w-full max-w-7xl mx-auto">
      <div className="grid grid-cols-1 md:grid-cols-12 gap-6">
        {/* Energy Summary Section */}
        <div className="md:col-span-5">
          <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow p-6`}>
            <h2 className="text-xl font-semibold mb-6">Energy Summary</h2>
            
            <div className="flex justify-between items-center mb-8">
              {/* Consumed Circle */}
              <div className="text-center">
                <div className="w-20 h-20 rounded-full border-8 border-gray-200 flex items-center justify-center">
                  <div className="text-center">
                    <div className="text-xl font-bold">{Math.round(totalNutrition.calories)}</div>
                    <div className="text-xs text-gray-500">kcal</div>
                  </div>
                </div>
                <div className="mt-2 text-sm">Consumed</div>
              </div>

              {/* Target Circle */}
              <div className="text-center">
                <div className="w-20 h-20 rounded-full border-8 border-green-500 flex items-center justify-center">
                  <div className="text-center">
                    <div className="text-xl font-bold">2049</div>
                    <div className="text-xs text-gray-500">kcal</div>
                  </div>
                </div>
                <div className="mt-2 text-sm">Target</div>
              </div>

              {/* Remaining Circle */}
              <div className="text-center">
                <div className="w-20 h-20 rounded-full border-8 border-gray-100 flex items-center justify-center">
                  <div className="text-center">
                    <div className="text-xl font-bold">{2049 - Math.round(totalNutrition.calories)}</div>
                    <div className="text-xs text-gray-500">kcal</div>
                  </div>
                </div>
                <div className="mt-2 text-sm">Remaining</div>
              </div>
            </div>

            {/* Targets Section */}
            <div className="space-y-4">
              <div className="flex justify-between items-center">
                <h3 className="text-lg font-semibold">Targets</h3>
                <h3 className="text-lg font-semibold">CONSUMED</h3>
              </div>

              <div className="space-y-4">
                <div className="flex justify-between items-center">
                  <span>Energy</span>
                  <span>{Math.round(totalNutrition.calories)} / 2049 kcal ({Math.round((totalNutrition.calories / 2049) * 100)}%)</span>
                </div>
                <div className="flex justify-between items-center">
                  <span>Protein</span>
                  <span>{Math.round(totalNutrition.protein)} / 153.7 g ({Math.round((totalNutrition.protein / 153.7) * 100)}%)</span>
                </div>
                <div className="flex justify-between items-center">
                  <span>Net Carbs</span>
                  <span>{Math.round(totalNutrition.carbs)} / 204.9 g ({Math.round((totalNutrition.carbs / 204.9) * 100)}%)</span>
                </div>
                <div className="flex justify-between items-center">
                  <span>Fat</span>
                  <span>{Math.round(totalNutrition.fat)} / 68.3 g ({Math.round((totalNutrition.fat / 68.3) * 100)}%)</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Search Vegan Foods Section */}
        <div className="md:col-span-7">
          <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow p-6`}>
            <h2 className="text-xl font-semibold text-green-700 mb-6">Search Vegan Foods</h2>
            
            <div className="mb-6">
              <div className="flex gap-2">
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Search for vegan foods..."
                  className={`flex-1 px-4 py-2 border-2 ${
                    darkMode 
                      ? 'bg-gray-700 border-gray-600 text-gray-200' 
                      : 'bg-white border-green-500 text-gray-900'
                  } rounded-md focus:ring-2 focus:ring-green-500 focus:border-transparent`}
                />
                <button
                  onClick={() => handleSearch(searchTerm)}
                  disabled={loading}
                  className="px-6 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
                >
                  Search
                </button>
              </div>
            </div>

            {!selectedFood && searchResults.length > 0 && (
              <div className="bg-white rounded-lg border border-gray-200 overflow-hidden">
                {searchResults.map((food) => (
                  <div
                    key={food.id}
                    className="px-4 py-3 hover:bg-green-50 cursor-pointer border-b last:border-b-0 flex justify-between items-center"
                    onClick={() => handleSelectFood(food)}
                  >
                    <span className="text-gray-800">{food.name}</span>
                    <span className="text-sm text-gray-500">Select â†’</span>
                  </div>
                ))}
              </div>
            )}

            {selectedFood && (
              <div className="bg-green-50 rounded-lg p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-green-800">{selectedFood.name}</h3>
                  <button 
                    className="text-green-600 hover:text-green-700"
                    onClick={() => setSelectedFood(null)}
                  >
                    Back to results
                  </button>
                </div>

                <div className="grid grid-cols-3 gap-4 mb-6">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-gray-800">{Math.round(selectedFood.nutrition?.protein || 0)}g</div>
                    <div className="text-sm text-gray-600">Protein</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-gray-800">{Math.round(selectedFood.nutrition?.carbs || 0)}g</div>
                    <div className="text-sm text-gray-600">Carbs</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-gray-800">{Math.round(selectedFood.nutrition?.fat || 0)}g</div>
                    <div className="text-sm text-gray-600">Fat</div>
                  </div>
                </div>

                <div className="text-center text-sm text-gray-600 mb-4">
                  Nutrition values per 100g serving
                </div>

                <button
                  onClick={() => {
                    onAddMeal(selectedFood);
                    setSelectedFood(null);
                    setSearchResults([]);
                    setSearchTerm('');
                    toast.success('Added to daily foods');
                  }}
                  className="w-full py-3 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
                >
                  Add to Daily Foods
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* ChatBot */}
      <ChatBot />
    </div>
  );
}

export default MealTracker;
