import React, { useState } from 'react';
import { PlusIcon, TrashIcon } from '@heroicons/react/24/outline';
import { toast } from 'react-toastify';
import axios from 'axios';

function MealTracker({ meals, onAddMeal, onRemoveMeal }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const searchFood = async (query) => {
    if (!query.trim()) return;
    
    setLoading(true);
    try {
      const response = await axios.get(`/api/food/search?query=${encodeURIComponent(query)}&vegan=true`);
      setSearchResults(response.data);
    } catch (error) {
      toast.error('Error searching for food items');
      console.error('Search error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddFood = async (food) => {
    try {
      const response = await axios.get(`/api/nutrition/calculate?id=${food.id}`);
      const mealWithNutrition = {
        ...food,
        nutrition: response.data
      };
      onAddMeal(mealWithNutrition);
      setSearchTerm('');
      setSearchResults([]);
      toast.success('Meal added successfully');
    } catch (error) {
      toast.error('Error adding meal');
      console.error('Add meal error:', error);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <h2 className="text-xl font-semibold mb-4">Track Your Meals</h2>
      
      {/* Search Bar */}
      <div className="mb-6">
        <div className="relative">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
              if (e.target.value.length >= 2) {
                searchFood(e.target.value);
              }
            }}
            placeholder="Search for vegan food..."
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-primary-500 focus:border-transparent"
          />
          {loading && (
            <div className="absolute right-3 top-2">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary-600"></div>
            </div>
          )}
        </div>

        {/* Search Results */}
        {searchResults.length > 0 && (
          <div className="mt-2 bg-white border border-gray-200 rounded-md shadow-lg">
            {searchResults.map((food) => (
              <div
                key={food.id}
                className="px-4 py-2 hover:bg-gray-50 flex items-center justify-between cursor-pointer"
                onClick={() => handleAddFood(food)}
              >
                <span>{food.name}</span>
                <PlusIcon className="h-5 w-5 text-primary-600" />
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Meals List */}
      <div className="space-y-4">
        {meals.map((meal, index) => (
          <div
            key={`${meal.id}-${index}`}
            className="flex items-center justify-between bg-gray-50 p-4 rounded-md"
          >
            <div>
              <h3 className="font-medium">{meal.name}</h3>
              <p className="text-sm text-gray-600">
                {meal.nutrition.calories} kcal | Protein: {meal.nutrition.protein}g | 
                Carbs: {meal.nutrition.carbs}g | Fat: {meal.nutrition.fat}g
              </p>
            </div>
            <button
              onClick={() => onRemoveMeal(index)}
              className="text-red-600 hover:text-red-800"
            >
              <TrashIcon className="h-5 w-5" />
            </button>
          </div>
        ))}

        {meals.length === 0 && (
          <p className="text-center text-gray-500 py-4">
            No meals added yet. Start by searching for food above!
          </p>
        )}
      </div>
    </div>
  );
}

export default MealTracker;
