import React, { createContext, useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const NutritionContext = createContext();

export const NutritionProvider = ({ children }) => {
  const navigate = useNavigate();
  const [userRequirements, setUserRequirements] = useState(null);
  const [selectedFoods, setSelectedFoods] = useState([]);

  const setRequirements = (calculatorResults) => {
    setUserRequirements({
      dailyCalories: calculatorResults.dailyCalories,
      macros: calculatorResults.macros,
      bmi: calculatorResults.bmi
    });
  };

  const addFood = (food) => {
    setSelectedFoods(prev => [...prev, food]);
    if (userRequirements) {
      setUserRequirements(prev => ({
        ...prev,
        dailyCalories: prev.dailyCalories,
        macros: {
          ...prev.macros,
          carbsGrams: prev.macros.carbsGrams,
          proteinGrams: prev.macros.proteinGrams,
          fatGrams: prev.macros.fatGrams
        }
      }));
    }
  };

  const removeFood = (foodId) => {
    setSelectedFoods(prev => prev.filter(food => food.id !== foodId));
    if (userRequirements) {
      setUserRequirements(prev => ({
        ...prev,
        dailyCalories: prev.dailyCalories,
        macros: {
          ...prev.macros,
          carbsGrams: prev.macros.carbsGrams,
          proteinGrams: prev.macros.proteinGrams,
          fatGrams: prev.macros.fatGrams
        }
      }));
    }
  };

  return (
    <NutritionContext.Provider 
      value={{ 
        userRequirements,
        setUserRequirements: setRequirements,
        selectedFoods,
        addFood,
        removeFood
      }}
    >
      {children}
    </NutritionContext.Provider>
  );
};

export const useNutrition = () => {
  const context = useContext(NutritionContext);
  if (!context) {
    throw new Error('useNutrition must be used within a NutritionProvider');
  }
  return context;
};
