import React from 'react';
import { Link } from 'react-router-dom';
import { SparklesIcon, SunIcon, MoonIcon } from '@heroicons/react/24/outline';
import { useNutrition } from '../context/NutritionContext';
import { useTheme } from '../context/ThemeContext';

function Header() {
  const { requirements } = useNutrition();
  const { darkMode, toggleDarkMode } = useTheme();
  
  return (
    <header className={`${darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'} shadow-sm transition-colors duration-200`}>
      <nav className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <SparklesIcon className={`h-8 w-8 ${darkMode ? 'text-green-400' : 'text-green-600'}`} />
            <Link to="/" className={`text-2xl font-bold hover:text-green-${darkMode ? '400' : '600'}`}>
              Vegan Meal Prep
            </Link>
          </div>
          <button 
            onClick={toggleDarkMode}
            className={`p-2 rounded-full ${darkMode ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-100 hover:bg-gray-200'} transition-colors`}
            aria-label="Toggle dark mode"
          >
            {darkMode ? (
              <SunIcon className="h-5 w-5 text-yellow-400" />
            ) : (
              <MoonIcon className="h-5 w-5 text-gray-600" />
            )}
          </button>
        </div>
      </nav>
    </header>
  );
}

export default Header;
