import React, { useState } from 'react';
import { useNutrition } from '../context/NutritionContext';
import { searchFood, getNutritionInfo } from '../utils/api';
import RequirementsPanel from './RequirementsPanel';

const FoodSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { selectedFoods, addFood, removeFood } = useNutrition();

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const results = await searchFood(searchTerm);
      setSearchResults(results);
    } catch (err) {
      console.error('Search error:', err);
      setError(err.userMessage || 'Failed to search foods. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleAddFood = async (food) => {
    try {
      const nutritionInfo = await getNutritionInfo(food.id);
      addFood({ ...food, nutrition: nutritionInfo });
    } catch (err) {
      console.error('Error adding food:', err);
      setError(err.userMessage || 'Failed to add food. Please try again.');
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Requirements Panel */}
        <div className="md:col-span-1">
          <RequirementsPanel />
        </div>

        {/* Search and Results */}
        <div className="md:col-span-2">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-2xl font-bold text-green-800 mb-6">Search Vegan Foods</h2>
            
            <form onSubmit={handleSearch} className="mb-6">
              <div className="flex gap-4">
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Search for vegan foods..."
                  className="flex-1 p-2 border border-gray-300 rounded-md"
                />
                <button
                  type="submit"
                  disabled={loading}
                  className="bg-green-600 text-white px-6 py-2 rounded-md hover:bg-green-700 transition-colors disabled:bg-green-400"
                >
                  {loading ? 'Searching...' : 'Search'}
                </button>
              </div>
            </form>

            {error && (
              <div className="mb-6 p-4 bg-red-100 text-red-700 rounded-md">
                {error}
              </div>
            )}

            {/* Search Results */}
            <div className="space-y-4">
              {searchResults.map((food) => (
                <div
                  key={food.id}
                  className="flex items-center justify-between p-4 border border-gray-200 rounded-md hover:bg-gray-50"
                >
                  <div>
                    <h3 className="font-medium">{food.name}</h3>
                    {food.nutrition && (
                      <p className="text-sm text-gray-600">
                        {Math.round(food.nutrition.calories)} cal | 
                        P: {Math.round(food.nutrition.protein)}g | 
                        C: {Math.round(food.nutrition.carbs)}g | 
                        F: {Math.round(food.nutrition.fat)}g
                      </p>
                    )}
                  </div>
                  <button
                    onClick={() => handleAddFood(food)}
                    className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors"
                  >
                    Add
                  </button>
                </div>
              ))}
            </div>

            {/* Selected Foods */}
            {selectedFoods.length > 0 && (
              <div className="mt-8">
                <h3 className="text-xl font-bold text-green-800 mb-4">Selected Foods</h3>
                <div className="space-y-4">
                  {selectedFoods.map((food) => (
                    <div
                      key={food.id}
                      className="flex items-center justify-between p-4 border border-gray-200 rounded-md bg-green-50"
                    >
                      <div>
                        <h3 className="font-medium">{food.name}</h3>
                        {food.nutrition && (
                          <p className="text-sm text-gray-600">
                            {Math.round(food.nutrition.calories)} cal | 
                            P: {Math.round(food.nutrition.protein)}g | 
                            C: {Math.round(food.nutrition.carbs)}g | 
                            F: {Math.round(food.nutrition.fat)}g
                          </p>
                        )}
                      </div>
                      <button
                        onClick={() => removeFood(food.id)}
                        className="text-red-600 hover:text-red-800"
                      >
                        Remove
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FoodSearch;
