import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useNutrition } from '../context/NutritionContext';
import { useTheme } from '../context/ThemeContext';

const Calculator = () => {
  const navigate = useNavigate();
  const { updateUserRequirements } = useNutrition();
  const { darkMode } = useTheme();
  const [formData, setFormData] = useState({
    gender: 'male',
    age: '',
    heightCm: '',
    weightKg: '',
    activityLevel: 'sedentary'
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [results, setResults] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const calculateRequirements = (data) => {
    // BMR calculation using Mifflin-St Jeor Equation
    let bmr;
    if (data.gender === 'male') {
      bmr = 10 * data.weightKg + 6.25 * data.heightCm - 5 * data.age + 5;
    } else {
      bmr = 10 * data.weightKg + 6.25 * data.heightCm - 5 * data.age - 161;
    }

    // Activity factor multipliers
    const activityFactors = {
      sedentary: 1.2,
      'lightly active': 1.375,
      'moderately active': 1.55,
      'very active': 1.725
    };

    const dailyCalories = bmr * activityFactors[data.activityLevel];
    const bmi = data.weightKg / Math.pow(data.heightCm / 100, 2);

    // Macronutrient calculations (adjusted for vegan diet)
    const protein = (dailyCalories * 0.3) / 4; // 30% of calories from protein
    const carbs = (dailyCalories * 0.4) / 4;   // 40% of calories from carbs
    const fat = (dailyCalories * 0.3) / 9;     // 30% of calories from fat

    return {
      dailyCalories,
      bmi,
      macros: {
        protein,
        carbs,
        fat
      }
    };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const calculatedResults = calculateRequirements(formData);
      setResults(calculatedResults);
      
      // Save the requirements to context
      updateUserRequirements({
        calories: Math.round(calculatedResults.dailyCalories),
        protein: Math.round(calculatedResults.macros.protein),
        carbs: Math.round(calculatedResults.macros.carbs),
        fat: Math.round(calculatedResults.macros.fat)
      });

      // Navigate to food search after a short delay
      setTimeout(() => {
        navigate('/');
      }, 2000);
    } catch (err) {
      console.error('Calculator error:', err);
      setError('Failed to calculate. Please check your inputs and try again.');
    } finally {
      setLoading(false);
    }
  };

  const getBMICategory = (bmi) => {
    if (bmi < 18.5) return { text: 'Underweight', color: darkMode ? 'text-yellow-400' : 'text-yellow-600' };
    if (bmi < 25) return { text: 'Normal weight', color: darkMode ? 'text-green-400' : 'text-green-600' };
    if (bmi < 30) return { text: 'Overweight', color: darkMode ? 'text-orange-400' : 'text-orange-600' };
    return { text: 'Obese', color: darkMode ? 'text-red-400' : 'text-red-600' };
  };

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900' : 'bg-gray-50'} py-12 px-4 sm:px-6 lg:px-8`}>
      <div className="max-w-md mx-auto">
        <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-lg p-6`}>
          <h2 className={`text-2xl font-bold mb-6 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            Calculate Your Requirements
          </h2>
          
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                Gender
              </label>
              <select
                name="gender"
                value={formData.gender}
                onChange={handleChange}
                className={`mt-1 block w-full rounded-md shadow-sm ${
                  darkMode 
                    ? 'bg-gray-700 border-gray-600 text-white' 
                    : 'bg-white border-gray-300 text-gray-900'
                } py-2 px-3`}
              >
                <option value="male">Male</option>
                <option value="female">Female</option>
              </select>
            </div>

            <div>
              <label className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                Age
              </label>
              <input
                type="number"
                name="age"
                value={formData.age}
                onChange={handleChange}
                className={`mt-1 block w-full rounded-md shadow-sm ${
                  darkMode 
                    ? 'bg-gray-700 border-gray-600 text-white' 
                    : 'bg-white border-gray-300 text-gray-900'
                } py-2 px-3`}
                required
              />
            </div>

            <div>
              <label className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                Height (cm)
              </label>
              <input
                type="number"
                name="heightCm"
                value={formData.heightCm}
                onChange={handleChange}
                className={`mt-1 block w-full rounded-md shadow-sm ${
                  darkMode 
                    ? 'bg-gray-700 border-gray-600 text-white' 
                    : 'bg-white border-gray-300 text-gray-900'
                } py-2 px-3`}
                required
              />
            </div>

            <div>
              <label className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                Weight (kg)
              </label>
              <input
                type="number"
                name="weightKg"
                value={formData.weightKg}
                onChange={handleChange}
                className={`mt-1 block w-full rounded-md shadow-sm ${
                  darkMode 
                    ? 'bg-gray-700 border-gray-600 text-white' 
                    : 'bg-white border-gray-300 text-gray-900'
                } py-2 px-3`}
                required
              />
            </div>

            <div>
              <label className={`block text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                Activity Level
              </label>
              <select
                name="activityLevel"
                value={formData.activityLevel}
                onChange={handleChange}
                className={`mt-1 block w-full rounded-md shadow-sm ${
                  darkMode 
                    ? 'bg-gray-700 border-gray-600 text-white' 
                    : 'bg-white border-gray-300 text-gray-900'
                } py-2 px-3`}
              >
                <option value="sedentary">Sedentary</option>
                <option value="lightly active">Lightly Active</option>
                <option value="moderately active">Moderately Active</option>
                <option value="very active">Very Active</option>
              </select>
            </div>

            <button
              type="submit"
              disabled={loading}
              className={`w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white ${
                loading
                  ? 'bg-gray-400'
                  : darkMode
                    ? 'bg-green-600 hover:bg-green-700'
                    : 'bg-green-600 hover:bg-green-700'
              } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500`}
            >
              {loading ? 'Calculating...' : 'Calculate'}
            </button>
          </form>

          {error && (
            <div className="mt-4 text-red-500 text-sm">
              {error}
            </div>
          )}

          {results && (
            <div className="mt-6 space-y-4">
              <div className={`p-4 rounded-lg ${darkMode ? 'bg-gray-700' : 'bg-green-50'}`}>
                <h3 className={`text-lg font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                  Your Results
                </h3>
                <div className="mt-2 space-y-2">
                  <p className={darkMode ? 'text-gray-300' : 'text-gray-600'}>
                    Daily Calories: {Math.round(results.dailyCalories)} kcal
                  </p>
                  <p className={darkMode ? 'text-gray-300' : 'text-gray-600'}>
                    BMI: {results.bmi.toFixed(1)} ({getBMICategory(results.bmi).text})
                  </p>
                  <div className={darkMode ? 'text-gray-300' : 'text-gray-600'}>
                    <p>Recommended Daily Macros:</p>
                    <ul className="ml-4">
                      <li>Protein: {Math.round(results.macros.protein)}g</li>
                      <li>Carbs: {Math.round(results.macros.carbs)}g</li>
                      <li>Fat: {Math.round(results.macros.fat)}g</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Calculator;
