import React, { useState } from 'react';
import { PlusIcon, TrashIcon, MagnifyingGlassIcon } from '@heroicons/react/24/outline';
import { toast } from 'react-toastify';
import { searchFood, getNutritionInfo } from '../utils/api';
import { useTheme } from '../context/ThemeContext';
import NutritionSummary from './NutritionSummary';
import ChatBot from './ChatBot';

function MealTracker({ meals, onAddMeal, onRemoveMeal }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const { darkMode } = useTheme();

  const handleSearch = async (query) => {
    if (!query || query.length < 2) {
      toast.info('Please enter at least 2 characters to search');
      setSearchResults([]);
      return;
    }

    setLoading(true);
    try {
      const results = await searchFood(query);
      setSearchResults(results || []);
      if (results?.length === 0) {
        toast.info('No food items found. Try a different search term.');
      }
    } catch (error) {
      // Only show error toast for network or server errors
      if (error.response?.status !== 404) {
        toast.error('Error searching for food items');
        console.error('Search error:', error);
      }
      setSearchResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSearch(searchTerm);
    }
  };

  const handleInputChange = (e) => {
    setSearchTerm(e.target.value);
    if (!e.target.value) {
      setSearchResults([]);
    }
  };

  const handleAddFood = async (food) => {
    try {
      const nutritionData = await getNutritionInfo(food.id);
      const mealWithNutrition = {
        ...food,
        nutrition: nutritionData
      };
      onAddMeal(mealWithNutrition);
      setSearchTerm('');
      setSearchResults([]);
      toast.success('Meal added successfully');
    } catch (error) {
      toast.error('Error adding meal');
      console.error('Add meal error:', error);
    }
  };

  return (
    <div className="w-full max-w-3xl">
      <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow p-6 transition-colors duration-200 mb-6`}>
        <h2 className={`text-xl font-semibold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>Track Your Meals</h2>
        
        {/* Search Bar */}
        <div className="mb-6">
          <div className="relative flex items-center">
            <input
              type="text"
              value={searchTerm}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="Search for vegan food (min. 2 characters)..."
              className={`w-full px-4 py-2 border ${
                darkMode 
                  ? 'bg-gray-700 border-gray-600 text-gray-200 placeholder-gray-400' 
                  : 'bg-white border-gray-300 text-gray-900'
              } rounded-l-md focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-colors duration-200`}
              disabled={loading}
            />
            <button
              onClick={() => handleSearch(searchTerm)}
              disabled={loading}
              className={`px-4 py-2 ${
                darkMode 
                  ? 'bg-green-600 hover:bg-green-700' 
                  : 'bg-primary-600 hover:bg-primary-700'
              } text-white rounded-r-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 disabled:opacity-50 transition-colors duration-200`}
            >
              {loading ? (
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
              ) : (
                <MagnifyingGlassIcon className="h-5 w-5" />
              )}
            </button>
          </div>

          {/* Search Results */}
          {searchResults.length > 0 && (
            <div className={`mt-2 ${
              darkMode 
                ? 'bg-gray-700 border-gray-600' 
                : 'bg-white border-gray-200'
            } border rounded-md shadow-lg max-h-60 overflow-y-auto transition-colors duration-200`}>
              {searchResults.map((food) => (
                <div
                  key={food.id}
                  className={`px-4 py-2 ${
                    darkMode 
                      ? 'hover:bg-gray-600 text-gray-200' 
                      : 'hover:bg-gray-50 text-gray-900'
                  } flex items-center justify-between cursor-pointer transition-colors duration-200`}
                  onClick={() => handleAddFood(food)}
                >
                  <span>{food.name}</span>
                  <PlusIcon className={`h-5 w-5 ${darkMode ? 'text-green-400' : 'text-primary-600'}`} />
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Meals List */}
        <div className="space-y-4">
          {meals.map((meal, index) => (
            <div
              key={`${meal.id}-${index}`}
              className={`flex items-center justify-between ${
                darkMode 
                  ? 'bg-gray-700 text-gray-200' 
                  : 'bg-gray-50 text-gray-900'
              } p-4 rounded-md transition-colors duration-200`}
            >
              <div>
                <h3 className="font-medium">{meal.name}</h3>
                <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                  {meal.nutrition.calories} kcal | Protein: {meal.nutrition.protein}g | 
                  Carbs: {meal.nutrition.carbs}g | Fat: {meal.nutrition.fat}g
                </p>
              </div>
              <button
                onClick={() => onRemoveMeal(index)}
                className={`${darkMode ? 'text-red-400 hover:text-red-300' : 'text-red-600 hover:text-red-800'} transition-colors duration-200`}
              >
                <TrashIcon className="h-5 w-5" />
              </button>
            </div>
          ))}

          {meals.length === 0 && (
            <p className={`text-center ${darkMode ? 'text-gray-400' : 'text-gray-500'} py-4 transition-colors duration-200`}>
              No meals added yet. Start by searching for food above!
            </p>
          )}
        </div>
      </div>

      {/* Nutrition Summary */}
      <div className="mb-6">
        <NutritionSummary totals={{
          calories: meals.reduce((sum, meal) => sum + (meal.nutrition?.calories || 0), 0),
          protein: meals.reduce((sum, meal) => sum + (meal.nutrition?.protein || 0), 0),
          carbs: meals.reduce((sum, meal) => sum + (meal.nutrition?.carbs || 0), 0),
          fat: meals.reduce((sum, meal) => sum + (meal.nutrition?.fat || 0), 0)
        }} />
      </div>

      {/* ChatBot */}
      <ChatBot />
    </div>
  );
}

export default MealTracker;
